
public class integers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int g = 5; //integer
		int o = 0; //totaler
		int k = g;
		
		o=o+g; //integer+totaler
		
		int l = 8; //integer
		
		g=-20; //integer
		
		o=o+g; //integer
		
		System.out.println("the total of " + k + " and " + l + " and " + g + " is " + o);
	}

}
