
public class ExamplesOfCounters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//example of a counter
		int n;
		n=8;
		System.out.println(n);
		n=n+1;
		System.out.println(n);
		n++;
		System.out.println(n);
		n++;
		System.out.println(n);
		n--;
		System.out.println(n);
		
		//example of a totaler
		int t;
		t=0;
		t=t+n;
		t+=n;
		t-=n;
		
		//example of a boolean
		
		boolean trial = (3<45)|| (5>9);
		
		
		
		
		
		
	}

}
